import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { CartService } from './cart.service';
import { OrderDetails } from './order-details';
import { ShoppingCart } from './shopping-cart';
import { User } from './user';

declare var Razorpay: any;
declare var swal: any;
@Injectable({
  providedIn: 'root',
})
export class UserService {
  constructor(
    private http: HttpClient,
    private cartService: CartService,
    private router: Router
  ) {}
  userSignIn(user: User): Observable<string> {
    return this.http.post('http://localhost:8081/register', user, {
      responseType: 'text',
    });
  }
  userLogIn(user: User): Observable<string> {
    return this.http.post('http://localhost:8081/authenticate', user, {
      responseType: 'text',
    });
  }
  refreshToken() {
    return this.http.get('http://localhost:8081/refreshtoken');
  }
  BuyTheProduct(
    amount: number,
    orderDetails: OrderDetails,
    cart: ShoppingCart | null = null
  ) {
    var options: any = {
      key: 'rzp_test_57H5LdGcUCbfNi',
      amount: amount * 100,
      currency: 'INR',
      name: 'E-Commerece Website',
      description: 'Test Transaction',
      image: this.imageLink,
      // "order_id": "order_9A33XWu170gUtm",
      // "handler": function (response:any){
      // swal("Payment Successfully", "This is a dummy Project","success");
      // console.log("success");
      // console.log(response);
      // // this.saveRecord
      // return response;
      // }.bind(this),
      prefill: {
        name: 'Subhasis Khuntia',
        email: 'subhasisKhuntia506@gmail.com',
        contact: '+91 7381779419',
      },
      notes: {
        address: 'JagatSinghpur, Odisha',
      },
      theme: {
        color: '#0072E1',
      },
    };
    options.handler = (response: any, error: any) => {
      options.response = response;
      console.log(response);
      console.log(options);
      orderDetails.paymentId = response.razorpay_payment_id;
      console.log(response.razorpay_payment_id);

      this.saveRecord(orderDetails).subscribe((result) => console.log(result));
      if (cart != null) {
        this.cartService.deleteAllCartItem(cart).subscribe(
          (result) => console.log(result),
          (error) => console.log(error),
          () => this.reloadCurrentRoute()
        );
      }
    };
    var rzp1: any = new Razorpay(options);
    rzp1.on('payment.failed', function (response: any) {
      alert(response.error.code);
      alert(response.error.description);
      alert(response.error.source);
      alert(response.error.step);
      alert(response.error.reason);
      alert(response.error.metadata.order_id);
      alert(response.error.metadata.payment_id);
      console.log('failed');
      return response;
    });

    this.http
      .post('http://localhost:8081/api/user/buyProduct', { amount: amount })
      .subscribe((result) => {
        console.log('called ');
        console.log(result);
        let values = JSON.parse(JSON.stringify(result));
        console.log(values.status);
        if (values.status == 'created') {
          rzp1.open();
          let response = options;
        }
      });
  }
  saveRecord(orderDetails: OrderDetails): Observable<string> {
    return this.http.post(
      'http://localhost:8081/api/user/saveRecord',
      orderDetails,
      { responseType: 'text' }
    );
  }
  reloadCurrentRoute() {
    let currentUrl = this.router.url;
    this.router.navigateByUrl('/', { skipLocationChange: true }).then(() => {
      this.router.navigate([currentUrl]);
    });
  }
  imageLink: string =
    'data:image/png;base64,';
}
